package codingPrograms;/** * 3: 31 = 3 * * 153: 13 + 53 + 33 = 1 + 125+ 27 = 153 * * 125: 13 + 23 + 53 = 1 + 8 + 125 = 134 (Not an Armstrong Number) */public class ArmstrongNumber {    public static void main(String...args){        System.out.println("5 is Armstrong Number "+isArmstrongNumber(5));        System.out.println("153 is Armstrong Number "+isArmstrongNumber(153));        System.out.println("370 is Armstrong Number "+isArmstrongNumber(370));        System.out.println("371 is Armstrong Number "+isArmstrongNumber(371));    }    private static Boolean isArmstrongNumber(int n){        int totalDigits = 0, temp = n, sum = 0;        while(temp > 0){            temp = temp/10;            totalDigits++;        }        temp = n;        while(temp > 0){            int num = temp%10;            sum+=Math.pow(num, totalDigits);            temp = temp/10;        }        return n == sum;    }}