package codingPrograms.codility;import java.util.*;/** * It is an Contact program * You will be provided 2 array - one contains N number of names and another contains N number of contact number * You will given one contact number (Not complete, its a substring of a contact number) * Find the matched contact number * If there are 2 matching names, then return sort the names alphabetically and return the first */public class FindMatchingContact {    public static void main(String...args){        FindMatchingContact obj = new FindMatchingContact();        String[] A = {"b","a","c","d","g","aa","ab"};        String[] B = {"123","123456","12","123","12345","12","12"};        System.out.println("OUT Put is+"+obj.silution(A,B,"123"));    }    public String silution(String[] A, String[] B, String P){        ArrayList matchedList = new ArrayList<String>();        for (int i = 0; i< B.length; i++){            if(B[i].contains(P)){                matchedList.add(A[i]);            }        }        if(matchedList.size()>0){            Collections.sort(matchedList);            return matchedList.get(0).toString();        }        return "NO CONTACT";    }}