package codingPrograms.codility;/** * You will be provided a String * Split the string in such a way that no duplication chat occur in each substring * Find minimum substring */public class FindNumberOfSubStrings {    public static void main(String...args){        FindNumberOfSubStrings obj = new FindNumberOfSubStrings();        System.out.println("Output Is "+obj.solution("dddd"));        System.out.println("Output Is "+obj.solution("world"));        System.out.println("Output Is "+obj.solution("Cycle"));    }    public int solution(String S){        int count = 0;        String[] strArray = S.split("");        String uniqueString = strArray[0];        for(int i = 0;i< strArray.length; i++){            if(!uniqueString.contains(strArray[i])){                uniqueString = uniqueString+strArray[i];            }            else{                uniqueString = ""+strArray[i];                count++;            }        }        return count;    }}