package codingPrograms.codility;import java.util.*;public class PalindromPosibilityInString {    public static boolean hasPalindrome(String str) {        HashMap<Character,Integer> charCountMap = new HashMap();        boolean hasPallindrom = false;        if(str.isEmpty() || str == null) return false;        else {            for(int i = str.length()-1 ; i >=0 ;i--){                Character ch = str.charAt(i);               if(charCountMap.containsKey(ch)){                   charCountMap.remove(ch);               }               else{                   charCountMap.put(ch,1);               }            }            if(str.length()%2==0){                hasPallindrom = charCountMap.size() == 0;            }            else{                    hasPallindrom = charCountMap.size() == 1;            }            return hasPallindrom;        }    }    public static void main(String [] args) {        boolean abba = hasPalindrome("abba");        boolean aabb = hasPalindrome("aabb");        boolean aabc = hasPalindrome("aaccccbbd");        System.out.println("hasPalindrome(abba) = " + abba);        System.out.println("hasPalindrome(aabb) = " + aabb);        System.out.println("hasPalindrome(aabc) = " + aabc);    }}