package codingPrograms.nagarro;public class FindCommonElementFrom3Array {	void findCommon(int ar1[], int ar2[], int ar3[])    {         int i = 0, j = 0, k = 0;          while (i < ar1.length && j < ar2.length && k < ar3.length)        {              if (ar1[i] == ar2[j] && ar2[j] == ar3[k])             {   System.out.print(ar1[i]+" ");   i++; j++; k++; }               else if (ar1[i] < ar2[j])                 i++;               else if (ar2[j] < ar3[k])                 j++;               else                 k++;        }    }      public static void main(String args[])    {    	FindCommonElementFrom3Array ob = new FindCommonElementFrom3Array();      	int ar1[] = {1, 2,5,7,8,11,19,21,22,28};        int ar2[] = {2,3,6,7,15,19,21,28,29,30};        int ar3[] = {2,7,8,15,19,21,22,23,28,30};    	        System.out.print("Common elements are ");        ob.findCommon(ar1, ar2, ar3);    }}