package codingPrograms.nagarro;import java.util.Scanner;import java.util.HashMap;import java.util.Map;import java.io.*;public class NumberOfCharacterOccurance {	public static void main(String args[]){		  Scanner sc=new Scanner(System.in);		  System.out.println("Enter String");		  String name=sc.nextLine();		  printOccurance(name);	}		public static void printOccurance(String name){		HashMap<Character,Integer> hashMap=new HashMap<>();		name=name.replace(" ","");		char[] array=name.toCharArray();		for(int i=0;i<array.length;i++) {			if(hashMap.containsKey(array[i])) {				hashMap.put(array[i], hashMap.get(array[i])+1);			}else{				hashMap.put(array[i], 1);			}		} 		for(Map.Entry entry:hashMap.entrySet()){			System.out.println(entry.getKey()+" "+entry.getValue());		}			} 	}