package codingPrograms.nagarro;import java.util.*;public class PrintVariableInFormat {		public static void convertToRequiredFormat(String inputString){		String javaOutPut=new String();		String outPut="";		if(inputString.contains("_")){			 String[] splitted=inputString.split("_");			 for(String x:splitted){				 javaOutPut+=getUpperCaseString(x);			 }			 outPut=toLowerCaseString(javaOutPut);		}else{			 char[] ch=inputString.toCharArray();			 String finaName="";			 for(int i=0;i<ch.length;i++){				 if(Character.isUpperCase(ch[i])){					 finaName+="_";					 finaName+=ch[i];				 }else{					 finaName+=ch[i];				 } 			 }			 outPut=finaName.toLowerCase(); 		}		System.out.println(outPut);	}		 static String getUpperCaseString(String name){		char[] charArray=name.toCharArray();		charArray[0]=Character.toUpperCase(charArray[0]); 		return new String(charArray);	  }	 	 static String toLowerCaseString(String name){			char[] charArray=name.toCharArray();			charArray[0]=Character.toLowerCase(charArray[0]); 			return new String(charArray);		  }	    static  String getCPlusName(String name){		return "";	}    	public static void main(String[] args){		System.out.println("Enter Variable Name: ");		Scanner sc=new Scanner(System.in);		String inputString=sc.next();		convertToRequiredFormat(inputString);	}	}