package codingPrograms.nagarro;public class SortArrayZeroAndOne {		public static void main(String args[]){		int[] arr={1,0,0,1,0,0,1,0,0,0,1};		sortArray(arr);	}	 	static void sortArray(int arr[]){		for(int i=0;i<arr.length;i++)		System.out.print(arr[i]+" ");		int j=0;		for(int i=0;i<arr.length;i++){			if(arr[i] == 0){				int temp=arr[j];				arr[j]=arr[i];				arr[i]=temp;				j++;			} 		}		System.out.println();		for(int i=0;i<arr.length;i++)			System.out.print(arr[i]+" ");			}}