package codingPrograms.nagarro;import java.util.regex.Matcher;import java.util.regex.Pattern;class SubStringCount{    /* Checks if a string is empty ("") or null. */    public static boolean isEmpty(String s) {        return s == null || s.length() == 0;    }     /* Counts how many times the substring appears in the larger string. */    public static int countMatches(String text, String str)    {        if (isEmpty(text) || isEmpty(str)) {            return 0;        }         Matcher matcher = Pattern.compile(str).matcher(text);         int count = 0;        while (matcher.find()) {            count++;        }         return count;    }     public static void main(String[] args)    {        String text = "AABCCAAADCBBAADBBC";        String str = "CCD";         int count = countMatches(text, str);        System.out.println(count);    }}