package linkedList;import java.util.Iterator;import java.util.LinkedList;public class Find3rdElementInLinkedList {    public static void main(String...args){        LinkedList<String> list = new LinkedList<>();        list.add("Siba1");        list.add("Siba2");        list.add("Siba3");        list.add("Siba4");        list.add("Siba5");        list.add("Siba6");        list.add("Siba7");        Iterator itr = list.iterator();        int position = 0;        while(itr.hasNext()){            position++;            if(position == 3) {                System.out.println("3rd element is ");            break;            }        }    }}