package recusrion;import java.util.ArrayList;import java.util.List;public class PrimeFactorOfANumber {    public static void main(String...args){        ArrayList<Integer> result = primeFactorOfNumber(10,2);        System.out.println(result);    }    private static ArrayList<Integer> primeFactorOfNumber(int n, int f){        if(n == 1) return new ArrayList<>();        else{            if(n%f == 0){                ArrayList<Integer> factors = primeFactorOfNumber(n/f, f);                factors.add(f);                return factors;            }            else{                return primeFactorOfNumber(n, f+1);            }        }    }}