package recusrion;public class SumOfNumberUptoN {    public static void main(String...args){        int result = sum(10);        System.out.println(result);        int result1 = sumWithoutResursive(10);        System.out.println("Without Recursion "+result1);    }    private static int sum(int n){        if(n > 0){            return  (n)+sum(n-1);        }        else{            return 0;        }    }    private static int sumWithoutResursive(int n){            return  (n)*(n+1)/2;    }}