package threadConcept;/** * The sleep() method of Thread class is used to sleep a thread for the specified amount of time. */public class SleepExample {    public static void main(String...args){        ThreadSleep t = new ThreadSleep();        ThreadSleep t1 = new ThreadSleep();        t.start();        t1.start();    }}class ThreadSleep extends Thread{    @Override    public void run() {        super.run();        for(int i=1;i<5;i++){            try{                Thread.sleep(5000);            }            catch(InterruptedException e){                System.out.println(e);            }            System.out.println(i);        }    }}