package threadConcept;/** * You can not start a same thread twice, you will get error * Execution failed for task ':StartThreadTwice.main()'. */public class StartThreadTwice {    public static void main(String...args){        ThreadTwice t = new ThreadTwice();        t.start();        t.start();    }}class ThreadTwice extends Thread{    @Override    public void run() {        System.out.println("ThreadTwice is running");    }}