package threadConcept;/** * The join() method waits for a thread to die. * In other words, it causes the currently running threads to stop executing until the thread it joins with completes its task. * * In the below example there are 3 threads t1, t2, t3 * t1.start() * t1.join(); * t2.start(); * t3.start(); * This means,WHen the t1 thread complete its task, then only t2, t3 will start * * */class ThreadJoinExample extends Thread{    public void run(){        for(int i=1;i<=5;i++){            try{                Thread.sleep(500);            }catch(Exception e){System.out.println(e);}            System.out.println(i);        }    }    public static void main(String args[]){        ThreadJoinExample t1=new ThreadJoinExample();        ThreadJoinExample t2=new ThreadJoinExample();        ThreadJoinExample t3=new ThreadJoinExample();        t1.start();        try{            t1.join();        }catch(Exception e) {            System.out.println(e);        }        t2.start();        t3.start();    }}